mapper.select:
SELECT * FROM post

    WHERE id >=(select id from post order by #{method} LIMIT #{pageNo}*#{pageSize},1)

ORDER BY #{method}
limit #{pageSize}

mapper.update:
UPDATE post
memberId=#{memberId}, title=#{title}, content=#{content}, datePublished=#{datePublished}, dateLastModified=#{dateLastModified}
WHERE id = #{id}
